import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Map.Entry;
import java.util.Iterator;
import connector.common.JsonUtil;
import sailpoint.connector.webservices.WebServicesClient;

//We can parse the response(rawResponseObject) to fetch from the respective executed Endpoint operation.
Map updatedMapInfo = new HashMap();
List Finallist = new ArrayList();
Map response = (Map) JsonUtil.toMap(rawResponseObject);
if(response != null) {
	int lastCreatedHBVEmployeeId = Integer.valueOf(response.get("connectorAttributes").get("lastCreateHBVEmployeeId"));
	int newHBVEmployeeId = lastCreatedHBVEmployeeId+1;
	Map responseMap = new HashMap();
	responseMap.put("lastCreatedHBVEmployeeId", lastCreatedHBVEmployeeId);
	responseMap.put("newHBVEmployeeId", newHBVEmployeeId);
	responseMap.put("action", response.get("name"));
	Finallist.add(responseMap);
	WebServicesClient client = new WebServicesClient();
	String url = requestEndPoint.getFullUrl();
	Map args = new HashMap();
	args.put(WebServicesClient.ARG_URL, url);
	client.configure(args);
	Map header = new HashMap();
	header.put("Authorization" , requestEndPoint.getHeader().get("Authorization"));
	header.put("Content-Type","application/json-patch+json");
	header.put("Accept","application/json");
	List<String> allowedStatuses = new ArrayList();
	allowedStatuses.add("2**");
	
	String payload = "[{\"op\": \"replace\",\"path\": \"/connectorAttributes/lastCreateHBVEmployeeId\",\"value\": \"" + newHBVEmployeeId + "\"}]";
	String response = client.executePatch(url, payload, header, allowedStatuses);
	
}
updatedMapInfo.put("data", Finallist);
return updatedMapInfo;
