import java.util.Map;

try {
	Map headerMap= requestEndPoint.getHeader();
	if(null != headerMap && headerMap.containsKey("Content-Type")) {
		headerMap.remove("Content-Type");
		requestEndPoint.setHeader(headerMap);
	}
} catch(Exception e) {
	log.error("Catch block running in OIC before operation rule:: " + e.getMessage(), e);
	throw new sailpoint.tools.GeneralException("Catch block running in OIC before operation rule:: " + e.getLocalizedMessage(), e);
}

return requestEndPoint;
