import sailpoint.connector.DelimitedFileConnector;
import java.util.Arrays;

Map map = DelimitedFileConnector.defaultBuildMap(cols, record);

// Handle disabled status based on End Date
String endDate = (String) map.get("End Date");
if (endDate != null && !endDate.trim().isEmpty()) {
    map.put("IIQDisabled", "true");
} else {    
    map.put("IIQDisabled", "false");
}

// Handle Security Groups
String securityGroups = (String) map.get("Security Groups");
if (securityGroups != null && !securityGroups.isEmpty()) {
    String[] groups = securityGroups.split(",");
    
    for (int i = 0; i < groups.length; i++) {
        groups[i] = groups[i].trim();
    }
    
    map.put("Security Groups", Arrays.asList(groups));
}

// Handle Field Level Security Types
String fieldLevelSecurityTypes = (String) map.get("Field Level Security Types");
if (fieldLevelSecurityTypes != null && !fieldLevelSecurityTypes.isEmpty()) {
    String[] types = fieldLevelSecurityTypes.split(",");
    
    for (int i = 0; i < types.length; i++) {
        types[i] = types[i].trim();
    }
    
    map.put("Field Level Security Types", Arrays.asList(types));
}

return map;
