public ArrayList getADAccountRequests(ProvisioningPlan plan) {
	ArrayList adAcctRequests = new ArrayList();
	if (null != plan && !plan.isEmpty()) {
		List accRequests = plan.getAccountRequests();
		if (null != accRequests && !accRequests.isEmpty()) {
			for (AccountRequest acctRequest : accRequests) {
				String appName = acctRequest.getApplication();
				if (null != appName && appName.startsWith(applicationName)) {
					adAcctRequests.add(acctRequest); 
				}
			}
		}
	}
	return adAcctRequests;
}
public String getLicenseToBeAssigned(String division, String empType, String managementLevel) {
	String corp = null;
	String licenseToBeAssigned = "";
	Map licenseMappingMap = (Map) application.getAttributeValue("licenseMapping");
	if(null != division) {
		corp = division.substring(0,4);
	}	
	if(null != licenseMappingMap) {
		Map mngLevelMap =licenseMappingMap.get("P|E");
		if(corp != null && corp.equalsIgnoreCase("CORP")){
			licenseToBeAssigned = licenseMappingMap.get("CorperateLicense");
        } else if((empType.equalsIgnoreCase("E") || empType.equalsIgnoreCase("P")) && null != managementLevel && mngLevelMap.get(managementLevel) != null){
			licenseToBeAssigned = mngLevelMap.get(managementLevel);
        } else if(empType.equalsIgnoreCase("C")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("V")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("Contractor")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("Vendor")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("Board of Directors")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("Regulator")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else if(empType.equalsIgnoreCase("Special Firms")) {
			licenseToBeAssigned = licenseMappingMap.get(empType);
        } else {
			licenseToBeAssigned = licenseMappingMap.get("DefaultLicense");
        }      
	}
	return licenseToBeAssigned;
}
public List getFilteredADGroups(String setupLikePersonId) {
	List setupLikeADGroupsFiltered = new ArrayList();
	if(null != setupLikePersonId) {
		List setupLikeADGroups = new ArrayList();
		Account setupLikeADAccount = idn.getFirstAccount(applicationName, setupLikePersonId);
		if(setupLikeADAccount != null) {
			Object setupLikeADGroupObj = idn.getRawAccountAttribute(setupLikeADAccount, "memberOf");
			if(null != setupLikeADGroupObj && setupLikeADGroupObj instanceof String) {
				setupLikeADGroups.add(String.valueOf(setupLikeADGroupObj));
			} else if(null != setupLikeADGroupObj && setupLikeADGroupObj instanceof List) {
				setupLikeADGroups = (List) setupLikeADGroupObj;
			}									
			if(null != setupLikeADGroups && !setupLikeADGroups.isEmpty()) {
				setupLikeADGroupsFiltered.addAll(setupLikeADGroups);
				String filterADGroupPattern = (String) application.getAttributeValue("filterADGroupPattern");
				for(String adGroup : setupLikeADGroups){
					Pattern findMyPattern = Pattern.compile(filterADGroupPattern, Pattern.CASE_INSENSITIVE);
					Matcher foundAMatch = findMyPattern.matcher(adGroup);											
					if(foundAMatch.find()){
						setupLikeADGroupsFiltered.remove(adGroup);
					}
				}
			}
		}
	}	
	return setupLikeADGroupsFiltered;
}
