public List getRBACProxyADGroups(String activeLocationCode, String personNumber, String managementLevelFlag) {
	List proxyADGroups = new ArrayList();
	boolean excLocFlag=false;
	boolean mngLevelFlag = "true".equalsIgnoreCase(managementLevelFlag);
	boolean divisionFlag=false;
	List assignments = (List) idn.getRawAccountAttribute(oicSourceName, personNumber, "assignments");								
	if(null != assignments && assignments instanceof List && !assignments.isEmpty()) {
		for(String assignment : assignments){
			String[] strArray=assignment.split("##");
			String division=strArray[2];
			if(division!=null && division.startsWith("Corp")){
				divisionFlag = true;
				break;
			}
		}
	}	
	if(null != activeLocationCode) {
		String[] activeLocationCodeArray = activeLocationCode.split(",");
		List adLocExclusionList = (List) application.getAttributeValue("adLocExclusionList");
		for(String activeLocation : activeLocationCodeArray) {
			if(null != adLocExclusionList && adLocExclusionList.contains(activeLocation.trim())) {
				excLocFlag = true;
				break;
			}
		}
	}								
	if(!excLocFlag && mngLevelFlag) {
		Map groupsMap = new HashMap();
		if(divisionFlag) {
			groupsMap = (Map) application.getAttributeValue("corpProxyUserADGroups");
		} else {
			groupsMap = (Map) application.getAttributeValue("proxyUserADGroups");
		}
		if(null != activeLocationCode) {
			String[] activeLocationCodeArray = activeLocationCode.split(",");
			for(String activeLocation : activeLocationCodeArray) {
				String proxyGroupName = groupsMap.get(activeLocation.trim());
				if(null != proxyGroupName) {
					proxyADGroups.add(proxyGroupName);
				}
			}
		}
	}
	return proxyADGroups;
}
