public boolean removeGroup(String group) {
	List o365Groups =  (List) application.getAttributeValue("o365Groups");
	String domainUsersGroup = "CN=Domain Users,OU";
	if(null != group && group.startsWith(domainUsersGroup)) {
		return true;
	}
	if(null != group && null != o365Groups && !o365Groups.isEmpty()) {
		for(String  o365Grp : o365Groups) {
			if(group.equalsIgnoreCase(o365Grp))
				return true;
		}
	}
	return false;
}
public boolean hasExchangeGrp(List groups) {
	boolean result = false;
	List o365Groups =  (List) application.getAttributeValue("o365Groups");
	if(null != groups && groups.size() != 0 && null != o365Groups){
		for(String group : groups) {
			if(null != o365Groups && o365Groups.size() != 0) {
				for(String  o365Grp : o365Groups) {
					if(group.equalsIgnoreCase(o365Grp)) {
						result = true;
						break;
					}
				}
			}
			if(result) {
				break;
			}
		}
	}
	return result;
}
public List removeGroup(List groups) {
    List returnList = new ArrayList();
	String domainUsersGroup = "CN=Domain Users,OU";
	List o365Groups =  (List) application.getAttributeValue("o365Groups");
    if(null != groups && groups.size() != 0 && null != o365Groups){
      for(String group : groups) {
		if(group.startsWith(domainUsersGroup))
			continue;
        boolean o365 = false;
        if(null != o365Groups && o365Groups.size() != 0) {
          for(String  o365Grp : o365Groups) {
            if(group.equalsIgnoreCase(o365Grp))
              o365=true;
          }
          if(!o365)
            returnList.add(group);
        }
      }
    }
    return returnList;
}
public List removeDomainUsersGroup(List groups) {
    List returnList = new ArrayList();
	String domainUsersGroup = "CN=Domain Users,OU";	
    if(null != groups && groups.size() != 0){
      for(String group : groups) {
		if(group.startsWith(domainUsersGroup))
			continue;
		returnList.add(group);
      }
    }
    return returnList;
}
public boolean isSetupLike(List attReqs, String sourceAttrName) {
	boolean result = false;
	if(null != attReqs && !attReqs.isEmpty()) {
		for(AttributeRequest attReq : attReqs) {
			if(attReq.getOperation() == add) {
				Object memberOf = attReq.getValue();
				if(null != memberOf) {
					String targetEnt = idn.getSourceAttributeBySourceName(application.getName(), sourceAttrName);
					if(null != targetEnt) {
						if(memberOf instanceof String && String.valueOf(memberOf).equalsIgnoreCase(targetEnt)) {
							result = true;
						} else if(memberOf instanceof List) {
							List groups = (List) memberOf;
							if(groups.contains(targetEnt)) {
								result = true;
							}
						}
					}
				}
			}
		}
	}	
	return result;
}
public String generatePassword() {
	String capitalCaseLetters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
	String lowerCaseLetters = "abcdefghijklmnopqrstuvwxyz";
    String numbers = "1234567890";
    String specialCharacters = "$#@*!^_-";
    int passwordLength = 14;
    char[] password = new char[passwordLength];
    String combinedChars = capitalCaseLetters + lowerCaseLetters + specialCharacters + numbers;
    Random random = new Random();

    password[0] = lowerCaseLetters.charAt(random.nextInt(lowerCaseLetters.length()));
    password[1] = capitalCaseLetters.charAt(random.nextInt(capitalCaseLetters.length()));
    password[2] = specialCharacters.charAt(random.nextInt(specialCharacters.length()));
    password[3] = numbers.charAt(random.nextInt(numbers.length()));

    for (int i = 4; i < passwordLength; i++) {
		password[i] = combinedChars.charAt(random.nextInt(combinedChars.length()));
	}
	return String.valueOf(password);
}
public String getAssignmentStartDateString(Object assignmentStartDateObj) {
