public Date getTerminationDate(Object termDateObj) {
	Date result = null;						
	if(null != termDateObj && termDateObj instanceof String && termDateObj != "") {
		SimpleDateFormat myFormat = new SimpleDateFormat("yyyy-MM-dd");
		result = myFormat.parse(termDateObj);
	} else if (null != termDateObj && termDateObj instanceof Date) {
		SimpleDateFormat myFormat = new SimpleDateFormat("yyyy-MM-dd");
		result = myFormat.parse(myFormat.format(termDateObj));
	}
	return result;
}
public AccountRequest leaverPlan(AccountRequest acctReq, String personNumber, String nativeIdentity, String action) {
	if(null != identity && null != personNumber && null != nativeIdentity && null != action && null != acctReq) {
		boolean exchangeDeprovNeeded = false;
		boolean terminationFlag = false;
		boolean legalHold = false;
		String harrahsLegalHoldGroup = (String) idn.getSourceAttributeBySourceName(applicationName, "harrahsLegalHoldGroup");
		String harrahsLegalHoldOU = (String) idn.getSourceAttributeBySourceName(applicationName, "harrahsLegalHoldOU");
		String contractorDisabledOUHarrahs = (String) idn.getSourceAttributeBySourceName(applicationName, "contractorDisabledOUHarrahs");
		String oldOUHarrahs = nativeIdentity.toUpperCase();
		String description = (String) idn.getRawAccountAttribute(applicationName, nativeIdentity, "description");
		if(null != description)
			description = description.toUpperCase();
		else
			description = "";
		String mailNickNameHarrahs = (String) idn.getRawAccountAttribute(applicationName, nativeIdentity, "mailNickname");
		String upn = (String) idn.getRawAccountAttribute(applicationName, nativeIdentity, "userPrincipalName");
		String samName = (String) idn.getRawAccountAttribute(applicationName, nativeIdentity, "sAMAccountName");
		String parentHarrahsOU = "";
		String newHarrahsOU = null;						
		if(null != mailNickNameHarrahs)
			exchangeDeprovNeeded = true;
		if(null != oldOUHarrahs && null != description &&
			description.indexOf("TERM(" ) == -1 &&
			description.indexOf("TERM (" ) == -1 &&
			oldOUHarrahs.indexOf("OU=SDS USERS" ) == -1 && 
			oldOUHarrahs.indexOf("OU=BOSS USERS" ) == -1
		) {
			terminationFlag = true;
			int i=oldOUHarrahs.indexOf("OU=USERS");
			if(i != -1) {
				char[] charArray=oldOUHarrahs.toCharArray();
				for(int j = i; j < oldOUHarrahs.length(); j++)
					parentHarrahsOU=parentHarrahsOU+charArray[j];
			}								
		}
		if(terminationFlag) {
			if("Employee".equalsIgnoreCase(workerType) && 
				null != employeeType && 
				(employeeType.equalsIgnoreCase("E") || employeeType.equalsIgnoreCase("P"))
			){
				if(null != parentHarrahsOU && !parentHarrahsOU.equals("")){
					newHarrahsOU = "OU=Disabled," + parentHarrahsOU;
				} else {
					newHarrahsOU = oldOUHarrahs;
				}
			} else if(null != contractorDisabledOUHarrahs){
				newHarrahsOU = contractorDisabledOUHarrahs;
			}							
			int intIndex =0;
			if(null != description) {
				intIndex= description.toUpperCase().indexOf("LEGAL HOLD");
				if(intIndex != - 1) {
					legalHold = true;
					if(null != harrahsLegalHoldGroup) {
						acctReq.add(new AttributeRequest("memberOf",add, harrahsLegalHoldGroup));
					}
				}
			}
			Object oldMemberOfValue = idn.getRawAccountAttribute(applicationName, nativeIdentity, "memberOf");
			if(
				"Employee".equalsIgnoreCase(workerType) &&
				null != employeeType && 
				null != oldMemberOfValue &&
				(employeeType.equalsIgnoreCase("E") || employeeType.equalsIgnoreCase("P"))
			) {
				if(oldMemberOfValue instanceof String) {
					String group = (String) oldMemberOfValue;
					boolean exchangeGrp = removeGroup(group);
					if(!exchangeGrp) {
						acctReq.add(new AttributeRequest("memberOf",remove,group));
						exchangeDeprovNeeded = false;
					} else {
						exchangeDeprovNeeded = true;
					}
				} else {
					List groups = (List) oldMemberOfValue;
					boolean hasExcGrp = hasExchangeGrp(groups);
					List exceptExchangeGrp = removeGroup(groups);									
					if(null != groups && !hasExcGrp) {
						exchangeDeprovNeeded = false;
						acctReq.add(new AttributeRequest("memberOf",remove,exceptExchangeGrp));
					} else {
						exchangeDeprovNeeded = true;
						acctReq.add(new AttributeRequest("memberOf",remove,exceptExchangeGrp));
					}
				}
			}
			else if(null != oldMemberOfValue) {
				if(oldMemberOfValue instanceof String) {
					String group = (String) oldMemberOfValue;
					if(null != group && !group.startsWith("CN=Domain Users,OU")) {
						acctReq.add(new AttributeRequest("memberOf",remove,group));
					}
				} else {
					List groups = (List) oldMemberOfValue;
					List exceptDomainUsersGrp = removeDomainUsersGroup(groups);
					acctReq.add(new AttributeRequest("memberOf", remove, exceptDomainUsersGrp));
				}
			}								
			if(
				null == oldMemberOfValue || 
				(!exchangeDeprovNeeded) || 
				(!employeeType.equalsIgnoreCase("E") && !employeeType.equalsIgnoreCase("P")) ||
				(!"Employee".equalsIgnoreCase(workerType))
			) {
				if(legalHold && null != harrahsLegalHoldOU) {
					acctReq.add(new AttributeRequest("AC_NewParent", set, harrahsLegalHoldOU));
				} else {
					acctReq.add(new AttributeRequest("AC_NewParent", set, newHarrahsOU));
				}
			}
			String oldDesc = (String) idn.getRawAccountAttribute(applicationName, nativeIdentity, "description");
			if(oldDesc == null)
				oldDesc = "";
			oldDesc = oldDesc.replaceAll("(?i)LOA\\([^)]*\\)", "").replaceAll("  +", " ").trim();
			Date date = new Date();
			SimpleDateFormat formatter = new SimpleDateFormat("MM/dd/yyyy");
			String todayDate = formatter.format(date);
			String termDescription = "TERM(" + todayDate + ") " + oldDesc;
			acctReq.add(new AttributeRequest("description", set, termDescription.trim()));
			String adTimeStamp = "never";
			if(null != terminationDate && terminationDate instanceof Date) {								
				Calendar cal = Calendar.getInstance();
				cal.setTime(terminationDate);
				cal.add(Calendar.DATE,1);
				long longDate = cal.getTime().getTime();
				longDate = (longDate * 10000L) + 116444736000000000L;
				longDate = longDate + 216000000000L;
				adTimeStamp = String.valueOf(longDate);
				acctReq.add(new AttributeRequest("accountExpires", set, adTimeStamp));
			} else {
				Date dt = new Date();
				Calendar cal = Calendar.getInstance();
				cal.setTime(dt);
				cal.add(Calendar.DATE,1);
				long longDate = cal.getTime().getTime();
				longDate = (longDate * 10000L) + 116444736000000000L;
				longDate = longDate + 216000000000L;
				adTimeStamp = String.valueOf(longDate);
				acctReq.add(new AttributeRequest("accountExpires", set, adTimeStamp));
			}
			acctReq.add(new AttributeRequest("manager", set, ""));
			acctReq.add(new AttributeRequest("userAccountControl", set, 512));
			acctReq.addArgument("excScriptUPN", upn);
			acctReq.addArgument("excScriptSAMAccName", samName);
			acctReq.addArgument("excScriptEmpType", employeeType);
			acctReq.addArgument("excScriptWorkerType", workerType);
			acctReq.addArgument("excScriptManagerDisplayName", managerDisplayName);
			acctReq.addArgument("excScriptManagerEmail", managerEmail);
			acctReq.addArgument("excScriptIdentityDisplayName", displayName);
			acctReq.addArgument("excScriptLifeCycleState", lifeCycleState);
			acctReq.addArgument("excScriptPersonNumber", personNumber);
			if(exchangeDeprovNeeded) {
				acctReq.addArgument("excScript", "true");
				acctReq.addArgument("excScriptName", "exchangeDeprovision");
				acctReq.addArgument("excScriptDomain", "H");
			}
		} else {							
			if(
				((oldOUHarrahs.indexOf("OU=SDS USERS" ) != -1) || (oldOUHarrahs.indexOf("OU=BOSS USERS" ) != -1)) && 
				null != description &&
				description.indexOf("TERM(" ) == -1 &&
				description.indexOf("TERM (" ) == -1
			) {
				acctReq.setOperation(modify);
				acctReq.addArgument("excScriptSendExclusionSDSBOSSEmail", "true");
			}
		}		
	}
	return acctReq;	
}
public AccountRequest modifyPlan(String personNumber, String nativeIdentity, String action, int legalHoldIndex) {
	AccountRequest acctReq = new AccountRequest();
	if(null != identity && null != personNumber && null != action) {		
		String newDescription = null;
		String newOU = null;
		String newPassword = null;
		String newManagerDN = null;
		String fName = identity.getFirstname();
		String lName = identity.getLastname();
		String pName = (String) identity.getAttribute("preferredName");
		String displayName = null;
		String suffix = (String) identity.getAttribute("employeeSuffix");
		String location = (String) identity.getAttribute("locationDescription");
		String company=null;
		String department=null;
		String dept = (String) identity.getAttribute("department");
		String jobTitle = (String) identity.getAttribute("jobTitle");					
		String division = (String) identity.getAttribute("division");
		String property = (String) identity.getAttribute("propertyCode");
		String mName = (String) identity.getAttribute("employeeMiddleName");
		String legalPay = (String) identity.getAttribute("LegalPaySystem");
		String assignStatusTypeCode = (String) identity.getAttribute("assignmentStatusTypeCode");
		String region = (String) identity.getAttribute("region");
		String businessUnitName = (String) identity.getAttribute("businessUnitName");
		String mngLevel = (String) idn.getRawAccountAttribute(oicSourceName, personNumber, "ManagementLevel");
		String locationCode = (String) identity.getAttribute("locationCode");
		String assignmentStatusType = (String) identity.getAttribute("assignmentStatusType");
