public String cleanData(String inputString) {
	if (inputString != null) {
		inputString = inputString.replaceAll("[^A-Za-z0-9]", "");
		inputString = StringUtils.trimToNull(inputString);
		if (inputString != null) {
			inputString = inputString.replaceAll("[\\s\\-()]", "");
			inputString = inputString.replaceAll("&#39;", "");
			inputString = inputString.toLowerCase();
		}
	}
	return inputString;
}
public boolean checkUnique(String cn) {
	boolean isCNUnique = false;
	String op = "StartsWith";
	String searchableCN = "searchableCN";
	String harrahsADSourceId = idn.getSourceAttributeBySourceName(application.getName(), "harrahsADSourceId");
	String whADSourceId = idn.getSourceAttributeBySourceName(application.getName(), "whADSourceId");
	String devADSourceId = idn.getSourceAttributeBySourceName(application.getName(), "devADSourceId");
	List SOURCE_IDS = new ArrayList(Arrays.asList(new String[] {
		harrahsADSourceId,
		whADSourceId,
		devADSourceId
	}));
	List SEARCH_VALUES = new ArrayList(Arrays.asList(new String[] {
		cn
	}));
	isCNUnique = idn.attrSearchCountAccounts(SOURCE_IDS, searchableCN, op, SEARCH_VALUES) == 0 ? true : false;
	return isCNUnique;
}
public String getCN(String fname, String lname) {
	boolean isUnique = false;
	String firstName = fname;
	String lastName = lname;
	if(firstName != null)
		firstName = firstName.trim();
	if(lastName != null)
		lastName = lastName.trim();
	String cn = new String();
	if(null != firstName && firstName.trim().length() > 0) {
		firstName = cleanData(firstName);
		if(null != firstName && firstName.trim().length() > 0) {
			if(firstName.length() > 1) {
				firstName = firstName.substring(0, 1).toUpperCase() + firstName.substring(1);
			} else {
				firstName = firstName.toUpperCase();
			}
		}
	}
	if(null != lastName && lastName.trim().length() > 0) {
		lastName = cleanData(lastName);
		if(null != lastName && lastName.trim().length() > 0) {
			if(lastName.length() > 1) {
				lastName = lastName.substring(0, 1).toUpperCase() + lastName.substring(1);
			} else {
				lastName = lastName.toUpperCase();
			}
		}
	}
	if(null == firstName || null == lastName || firstName.trim().length() == 0 || lastName.trim().length() == 0) {
		return cn;
	}
	cn = firstName + " " + lastName;
	String tempCN = cn;
	for(int count = 1; count < 1000; count++) {
		isUnique = checkUnique(tempCN);
		if(isUnique) {
			cn = tempCN;
			break;
		}
		tempCN = firstName + " " + lastName + count;
	}
	return cn;
}
