String employeeNumber = account.getStringAttribute("employeeNumber");
String trimmedEmployeeNumber = "";
if (employeeNumber != null && !employeeNumber.isEmpty()) {
    int length = employeeNumber.length();
    int i;
    // Start from the last character and move backwards
    for (i = length - 1; i >= 0; i--) {
        if (!(employeeNumber.charAt(i) >= 'A' && employeeNumber.charAt(i) <= 'Z')) {
            break;
        }
    }
    trimmedEmployeeNumber = employeeNumber.substring(0, i + 1);
}

Map returnMap = new HashMap();
returnMap.put("identityAttributeName", "identificationNumber");
returnMap.put("identityAttributeValue", trimmedEmployeeNumber);

return returnMap;
